package com.tearoffcalendar.activities;

import java.util.Date;

import android.app.Activity;
import android.content.Context;
import android.content.Intent;
import android.content.SharedPreferences;
import android.os.Bundle;
import android.util.Log;
import android.view.Menu;
import android.view.MotionEvent;
import android.view.View;
import android.view.View.OnTouchListener;
import android.webkit.WebView;

import com.example.android.lifecycle.R;
import com.tearoffcalendar.app.TearOffApp;
import com.tearoffcalendar.themes.BasicTheme;
import com.tearoffcalendar.themes.BasicThemeManager;
import com.tearoffcalendar.themes.ThemeException;

public class TextCardActivity extends DisplayMenuActivity implements OnTouchListener {

	private WebView mMainView;

	private float deltaY = 0;
	private float clickStartedOnY = 0;
	private static final int DELTA_Y_MAX = 100;

	private static final String TAG = "TextCardActivity";
	private static final BasicThemeManager themeManager = TearOffApp
			.getInstance().getThemeManager();
	private BasicTheme theme;

	@Override
	public void onCreate(Bundle savedInstanceState) {
		super.onCreate(savedInstanceState);
		setContentView(R.layout.activity_text_card);
		mMainView = (WebView) findViewById(R.id.text_card_web_view);
		mMainView.setBackgroundColor(0);

		// We need to know card id here.
		Intent intent = getIntent();
		Date date = null;
		int cardId = intent.getIntExtra(TestImageViewActivity.IMAGE_KEY, -1);
		if (-1 == cardId) {
			// Throw something! But we can't throw from onCreate() can we ?
		} else {
			Log.v(TAG, "Card id: " + String.valueOf(cardId));
			date = TestImageViewActivity.convertIdToDate(cardId);
			Log.v(TAG, "Date: " + String.valueOf(date));
		}

		SharedPreferences sharedPref = this.getSharedPreferences(
				getString(R.string.preference_file_key), Context.MODE_PRIVATE);
		String themeNameKey = getString(R.string.current_theme_key);
		String currentThemeName = sharedPref.getString(themeNameKey, "");
		if (currentThemeName.isEmpty()) {
			// Throw something! But we can't throw from onCreate() can we ?
		} else {
			try {
				Log.v(TAG, "Current theme name: " + currentThemeName);
				theme = themeManager.getThemeByName(currentThemeName);
				mMainView.loadDataWithBaseURL("file:///android_res/raw/",
						theme.getTextCard(date), "text/html", "utf-8", null);
				mMainView.setOnTouchListener(this);
			} catch (ThemeException te) {
				Log.e(TAG, te.getMessage());
			}
		}
		// TODO: html code should be provided by a ThemeManager;
		// code below is for demonstration only and will be removed; no need to
		// extract this html to reources manually
		// mMainView
		// .loadDataWithBaseURL(
		// "file:///android_res/raw/",
		// "<html><head></head><body><table><tr><td><font size=\"32pt\">道</font></td><td>Перед вами исключительный по красоте и значимости знак, который в самом обыденном своем смысле обозначает обычную дорогу, тропу или, в крайнем случае, тропинку. Чуть в более широком  значении – путь. И уж совсем в философском плане этот иероглиф  символизирует не просто путь,   а    путь   с самой большой  буквы  «П».</td></tr></table>"
		// + "<br /> Image example below:<br />"
		// +
		// "<img src=\"data:image/png;base64,\" alt=\"cemetery.png\" />"
		// + "</body></html>", "text/html", "utf-8", null);
	}

	@Override
	public boolean onCreateOptionsMenu(Menu menu) {
		getMenuInflater().inflate(R.menu.activity_text_card, menu);
		return true;
	}

	public boolean onTouch(View view, MotionEvent event) {
		final float y = event.getRawY();
		switch (event.getAction() & MotionEvent.ACTION_MASK) {
		case MotionEvent.ACTION_DOWN:
			Log.v(TAG, "Touch: Action down!");
			deltaY = 0;
			clickStartedOnY = y;
			Log.v(TAG, "Click started on:" + String.valueOf(clickStartedOnY));
			break;
		case MotionEvent.ACTION_UP:
			Log.v(TAG, "Resulted deltaY:" + String.valueOf(deltaY));
			// This one is for when finger is up, not a rude gesture bro!
			if (deltaY >= DELTA_Y_MAX) {
				Log.v(TAG, "Greater than max of delta y - not a click!");
			} else {
				Log.v(TAG, "Smaller than max of delta y - a click!");
				Intent intent = new Intent(TextCardActivity.this,
						TestImageViewActivity.class);
				startActivity(intent);
			}
			deltaY = clickStartedOnY = 0;
			break;
		case MotionEvent.ACTION_POINTER_DOWN:
			Log.v(TAG, "Touch: Action pointer down!");
			break;
		case MotionEvent.ACTION_POINTER_UP:
			Log.v(TAG, "Touch: Action pointer up!");
			break;
		case MotionEvent.ACTION_MOVE:
			Log.v(TAG, "Touch: Action move!");
			deltaY += Math.abs(y - clickStartedOnY);
			Log.v(TAG, "Current delta: " + String.valueOf(deltaY));
			break;
		}
		return false;
	}
}
